import axios from 'axios';
import React, { Component } from 'react';


interface propsType{
    children: React.DetailedReactHTMLElement<any, HTMLElement>
}

interface stateType{
    pendingApiCall:boolean
}

class ApiProgress extends Component<propsType> {


    state :stateType= {
        pendingApiCall: false
    }


    componentDidMount() {

        axios.interceptors.request.use((request) => {
            this.setState({
                pendingApiCall: true
            });
            return request;
        });

        axios.interceptors.response.use((response) => {
            this.setState({
                pendingApiCall: false
            });
            return response;
        }, (er) => {
            this.setState({
                pendingApiCall: false
            });
            throw er;
        })
    }


    render() {
        return (
            <div>
                {React.cloneElement(this.props.children,{penndingApiCall:this.state.pendingApiCall})}
            </div>
        );
    }
}

export default ApiProgress;
