import { withTranslation } from 'react-i18next';
import UserSignUpPage from '../pages/UserSignUp';
import LanguageSelector from './LanguageSelector';
import LoginPage from '../pages/LoginPage';
import { HashRouter as Router, Redirect, Route, RouteProps, Switch } from 'react-router-dom'
import HomePage from '../pages/HomePage';
import UserPage from '../pages/UserPage';
import TopBar from './TopBar';
import React from 'react';


class App extends React.Component {

  state = {
    isLoggedIn: false,
    username: undefined
  }

  onLoginSuccess = (username: string) => {
    this.setState(
      {
        username,
        isLoggedIn: true
      }
    )
  }

  render() {

    const { isLoggedIn, username } = this.state as any;

    return (
      <div className='container'>

        <Router >
          <TopBar username={username} isLoggedIn={isLoggedIn} />
          <Switch>
            <Route exact path="/" component={HomePage} />
            {!isLoggedIn && <Route path="/login" component={(props: RouteProps) => {
              return <LoginPage {...props} onLoginSuccess={this.onLoginSuccess} />
            }} />}
            <Route path="/signup" component={UserSignUpPage} />
            <Route path="/user/:username" component={UserPage} />
            <Redirect to={"/"} />
          </Switch>
        </Router>
        <LanguageSelector />
      </div>
    );
  }


}

export default withTranslation()(App);
