import { withTranslation } from 'react-i18next';
import UserSignUpPage from '../pages/UserSignUp';
import LanguageSelector from './LanguageSelector';
import LoginPage from '../pages/LoginPage';
import { HashRouter as Router, Redirect, Route, RouteProps, Switch } from 'react-router-dom'
import HomePage from '../pages/HomePage';
import UserPage from '../pages/UserPage';
import TopBar from './TopBar';
import React from 'react';
import { Authentication } from '../common/AuthenticationContext';


class App extends React.Component {


  render() {


    return (<Authentication.Consumer>
      {
        value => {
          return (
            <div className='container'>

              <Router >
                <TopBar/>
                <Switch>
                  <Route exact path="/" component={HomePage} />
                  {!value.authState.isLoggedIn && <Route path="/login" component={(props: RouteProps) => {
                    return <LoginPage {...props} />
                  }} />}
                  <Route path="/signup" component={UserSignUpPage} />
                  <Route path="/user/:username" component={(props: RouteProps) => {
                    return <UserPage {...props} />;
                  }} />
                  <Redirect to={"/"} />
                </Switch>
              </Router>
              <LanguageSelector />
            </div>
          );
        }
      }

    </Authentication.Consumer>);


  }


}

export default withTranslation()(App);
