import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import defaultImage from '../assets/profile.png'

const ProfileCard = (props: any) => {

    // const context = useContext(Authentication);
    // const {state} = context as any ;
    // const { username } = state;

    let message = "YOU CONNOT EDIT";
    const routerParams = useParams();
    const { username, displayname, image } = useSelector((store: any) => {
        return { username: store.username, displayname: store.displayname, image: store.image }
    })

    let defaultImg = image ? image : defaultImage;

    const userPath: string = (routerParams as any).username;
    if (userPath === username) {
        message = "YOU CAN EDIT";
    }

    return (
        <div className='card text-center'>
            <div className='card-header'>
                <img src={defaultImg} alt='profil' width={200} />

            </div>
            <div className='card-body'>
                {displayname + " - " + username}

            </div>
        </div>
    );
};

export default ProfileCard;
