import React from 'react';
import defaultImage from '../assets/profile.png'


const ProfileImage = (props:any) => {

    const {imageDb,width, tempimage} = props;
    let imgsrc = imageDb || defaultImage;
    if(imageDb)
    {
        imgsrc = "/images/"+ imageDb;
    }
    const image = tempimage || imgsrc;
    return (
        <img src={image} alt='profil' width={width} className="rounded-circle shadow" 
        onError={(event :any)=>{
            event.target.src = defaultImage;
        }}/>
    );
};

export default ProfileImage;
