import { withTranslation } from "react-i18next";
import Input from "../component/Input";
import React from "react";
import { login } from "../api/ApiCalls";
import ButtonWithLoading from "../component/ButtonWithLoading";
import { withApiProgress } from "../common/ApiProgress";
import { Authentication } from "../common/AuthenticationContext";
import { connect } from "react-redux";

class LoginPage extends React.Component<any, any> {

    //static contextType: React.Context<any> = Authentication;

    state = {
        username: null,
        password: null,
        error: null,
    }

    onChange = (event: any) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
            error: null
        });
    }

    onLoginSuccess =(auth:any)=>
    {
        const action ={
            type:"login",
            payload:auth
        }

        this.props.dispatch(action);

    }

    onClick = async (event: any) => {
        event.preventDefault();
        this.setState({ error: null });
        const { username, password } = this.state;
        const { history } = this.props as any;
        //const { onLoginSuccess } = this.context as any;
        const cred = {
            username,
            password
        }
        try {
            const res: any = await login(cred);
            const authState = {
                ...res.data,
                password
            }
            this.onLoginSuccess(authState);
            history.push("/");

        } catch (err :any) {
            this.setState({
                error: err.response.data.message
            });

        }
    }

    render() {
        const { t, pendingApiCall } = this.props as any;
        const { username, password, error } = this.state;
        const enableLogin = !Boolean(username && password) && pendingApiCall;

        return (
            <div className="container">
                <h1 className="text-center">{t("Login")}</h1>
                <form>
                    <Input name={"username"} label={t("Username")} onChange={this.onChange} />
                    <Input name={"password"} label={t("Password")} onChange={this.onChange} type={"password"} />
                    <ButtonWithLoading onClick={this.onClick} disabled={enableLogin} text={t("Login")} pendingApiCall={pendingApiCall} error={error} />
                </form>

            </div>
        )
    }
}

const mapStateToProps =(store:any) =>
{

}

export default connect(mapStateToProps)(withApiProgress(withTranslation()(LoginPage), "/api/1.0/auth"));
