import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Authentication } from '../common/AuthenticationContext';


const ProfileCard = (props: any) => {

    let message = "YOU CONNOT EDIT";
    const userPath: string = props.match.params.username;

    if (userPath === props.username) {
        message = "YOU CAN EDIT";
    }

    return (
        <div>
            {message}
        </div>
    );
};


class ProfileCardWrapper extends Component<any, any> {

    static contextType?: React.Context<any> | undefined = Authentication;

    render() {
        return (
            <ProfileCard username={(this.context as any).state.username} />
        );
    }
}


