import { useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useParams } from 'react-router-dom';
import { updateUser } from '../api/ApiCalls';
import defaultImage from '../assets/profile.png'
import { useApiProgress } from '../common/ApiProgress';
import ButtonWithLoading from './ButtonWithLoading';
import Input from './Input';

const ProfileCard = (props: any) => {

    // const context = useContext(Authentication);
    // const {state} = context as any ;
    // const { username } = state;
    const [editMode, setEditMode] = useState(false);
    const routerParams = useParams();
    const userPath: string = (routerParams as any).username;
    const { t } = useTranslation();
    const [users, setUsers] = useState({});
    const { username, displayname, image } = users as any;

    const [updatedName, setUpdatedName] = useState(displayname);
    let defaultImg = image ? image : defaultImage;
    const pendingApiCall = useApiProgress("put", `/api/1.0/users/${username}`);

    useEffect(() => {
        setUsers({ ...props.user });
    }, [props.user]);

    const onSaveClick = () => {
        try {
            updateUser(userPath, { displayname: updatedName }).then(response => {
                setUsers(response.data);
                setEditMode(false);

            });
        } catch (error) {

        }

    }

    useEffect(() => {
        if (editMode) setUpdatedName(displayname);
        else setUpdatedName(undefined);

    }, [editMode, displayname]);

    debugger;

    const editable = userPath === username;
    debugger;


    return (
        <div className='card text-center'>
            <div className='card-header'>
                <img src={defaultImg} alt='profil' width={200} className="rounded-circle shadow" />
            </div>
            <div className='card-body'>

                {editMode && (
                    <>
                        <Input label={t("Change Display Name")} defaultValue={displayname} onChange={(event: any) => { setUpdatedName(event.target.value) }} />
                        <ButtonWithLoading className='btn btn-primary d-inline-flex mt-2' onClick={onSaveClick} pendingApiCall={pendingApiCall}
                        text={
                            <>
                                <span className="iconify mt-1 me-1" data-icon="mdi:content-save"></span>
                                {t("Save")}
                            </>
                        }>
                        </ButtonWithLoading>
                        <button className='btn btn-secondary d-inline-flex mt-2 ms-2' onClick={() => { setEditMode(false) }}>
                            <span className="iconify mt-1 me-1" data-icon="mdi:close-thick"></span>{t("Cancel")}</button>
                    </>
                )}
                {editable && !editMode && (<>
                    <h4>{displayname + " - " + username}</h4>
                    <button className='btn btn-primary d-inline-flex' onClick={() => { setEditMode(true) }}>
                        <span className="iconify mt-1 me-1" data-icon="mdi:pencil">
                        </span>
                        {t("Edit")}</button>
                </>)}

            </div>
        </div>
    );
};

export default ProfileCard;
