import { useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { updateUser } from '../api/ApiCalls';
import defaultImage from '../assets/profile.png'
import Input from './Input';

const ProfileCard = (props: any) => {

    // const context = useContext(Authentication);
    // const {state} = context as any ;
    // const { username } = state;
    const [editMode, setEditMode] = useState(false);
    let message = "YOU CONNOT EDIT";
    const routerParams = useParams();
    const userPath: string = (routerParams as any).username;
    const { t } = useTranslation();
    const [user, setUser] = useState({ ...props.user });
    const [updatedName, setUpdatedName] = useState(user.displayname);
    let defaultImg = user.image ? user.image : defaultImage;

    if (userPath === user.username) {
        message = "YOU CAN EDIT";
    }

    const onSaveClick = async () => {
        try {
            const response = await updateUser(userPath, { displayname: updatedName });
            debugger;
            setUser({...response.data});
            setEditMode(false);
        } catch (error) {

        }

    }

    useEffect(() => {
        if (editMode) setUpdatedName(user.displayname);
        else setUpdatedName(undefined);

    }, [editMode, user.displayname])

    return (
        <div className='card text-center'>
            <div className='card-header'>
                <img src={defaultImg} alt='profil' width={200} className="rounded-circle shadow" />
            </div>
            <div className='card-body'>

                {editMode && (
                    <>
                        <Input label={t("Change Display Name")} defaultValue={user.displayname} onChange={(event: any) => { setUpdatedName(event.target.value) }} />
                        <button className='btn btn-primary d-inline-flex mt-2' onClick={onSaveClick}>
                            <span className="iconify mt-1 me-1" data-icon="mdi:content-save"></span>{t("Save")}</button>
                        <button className='btn btn-secondary d-inline-flex mt-2 ms-2' onClick={() => { setEditMode(false) }}>
                            <span className="iconify mt-1 me-1" data-icon="mdi:close-thick"></span>{t("Cancel")}</button>
                    </>
                )}
                {!editMode && (<>
                    <h4>{user.displayname + " - " + user.username}</h4>
                    <button className='btn btn-primary d-inline-flex' onClick={() => { setEditMode(true) }}>
                        <span className="iconify mt-1 me-1" data-icon="mdi:pencil">
                        </span>
                        {t("Edit")}</button>
                </>)}

            </div>
        </div>
    );
};

export default ProfileCard;
