import axios from 'axios';
import React, { Component } from 'react';


interface propsType{
    children: any,
    path:string
}

interface stateType{
    pendingApiCall:boolean
}

class ApiProgress extends Component<propsType> {


    state :stateType= {
        pendingApiCall: false
    }

    updateState = (url:string,durum:boolean) =>
    {
        if(this.props.path && url === this.props.path)
        {
            this.setState({
                pendingApiCall: durum
            });
        }
    }


    componentDidMount() {

        axios.interceptors.request.use((request) => {

            this.updateState(request.url!,true);
            return request;
        });

        axios.interceptors.response.use((response) => {
            this.updateState(response.config.url!,false);
            return response;
        }, (er) => {
            this.updateState(er.config.url!,false);
            throw er;
        })
    }


    render() {
        return (
            <div>
                {React.cloneElement(this.props.children,{pendingApiCall:this.state.pendingApiCall})}
            </div>
        );
    }
}

export default ApiProgress;
