import React, { Component, useContext } from 'react';
import { withTranslation } from 'react-i18next';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png'
import { Authentication } from '../common/AuthenticationContext';
import { logoutAction } from '../redux/authAction';

const TopBar = (props:any) => {

    //static contextType: React.Context<any> = Authentication;
    const context = useContext(Authentication);
    const {state,onLogOut:onLogOutSuccess} = context as any ;
    const { isLoggedIn, username } = state;

    console.log(props)
    const { t } = props as any;
    //
    //const { isLoggedIn, username, onLogOutSuccess } = props;


    let link = (<div className="collapse navbar-collapse col-md-9 offset-md-9" id="navbarNavAltMarkup">
        <div className="navbar-nav">
            <ul className="navbar-nav ml-auto" style={{ "display": "inline-block" }}>
                <li className="nav-item active">
                    <Link className="nav-link" to="/login">{t("Login")}</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/signup">{t("Sign Up")}</Link>
                </li>
            </ul>
        </div>
    </div>)


    if (isLoggedIn) {

        link = (<div className="collapse navbar-collapse col-md-9 offset-md-9" id="navbarNavAltMarkup">
            <div className="navbar-nav">
                <ul className="navbar-nav ml-auto" style={{ "display": "inline-block" }}>
                    <li className="nav-item active">
                        <Link className="nav-link" to="/" onClick={onLogOutSuccess}  >{t("Logout")}</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to={`/user/${username}`}>{username}</Link>
                    </li>
                </ul>
            </div>
        </div>)

    }


    return (
        <nav className="navbar navbar-expand navbar-light bg-light">
            <Link className='navbar-brand' to={"/"}>
                <img src={logo} alt="logo" width={100} />PazarFy
            </Link>
            {link}
        </nav>

    );

}

const mapStateToProps = (store: any) => {
    return {
        username: store.username,
        isLoggedIn: store.isLoggedIn
    }

}

const mapDispatchToProps = (dispatch: any) => {
    return {
        onLogOutSuccess: () => { dispatch(logoutAction) }
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(withTranslation()(TopBar));
