import { withTranslation } from "react-i18next";
import Input from "../component/Input";
import React from "react";
import { login } from "../api/ApiCalls";

class LoginPage extends React.Component {

    state = {
        username: null,
        password: null,
        pendingApiCall: false,
        errors: {}
    }

    onChange = (event :any) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }

    onClick = async (event :any) => {
        event.preventDefault();
        const { username, password } = this.state;
        const cred = {
            username,
            password
        }

        try {
            const res = await login(cred);
        } catch (error) {

        }
    }

    onChangeLanguage = (lang:any) => {
        const errors = {};
        this.setState({ errors });
        const { i18n } = this.props as any;
        i18n.changeLanguage(lang);
    }

    render() {
        const { t } = this.props as any;
        const { pendingApiCall, errors } = this.state;
        const { username, password } = errors as any;
        return (
            <div className="container">
                <h1 className="text-center">{t("Login")}</h1>
                <form>
                    <Input name={"username"} error={username} label={t("Username")} onChange={this.onChange} />
                    <Input name={"password"} error={password} label={t("Password")} onChange={this.onChange} type={"password"} />
                    <div className="form-group text-center">
                        <button className="btn btn-primary" onClick={this.onClick} disabled={pendingApiCall}>{t("Login")}
                            {pendingApiCall && <span className="spinner-border spinner-border-sm"></span>}
                        </button>
                    </div>
                </form>

            </div>
        )
    }
}

export default LoginPage;
