import React, { useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';
import { getFeeds } from '../api/ApiCalls';
import FeedItem from './FeedItem';

const FeedList = () => {

    const [pagingFeed, setPagingFeed] = useState({
        content: [],
        last: true,
        number: 0
    });
    const { t } = useTranslation();

    const loadFeed = async (page = 0, size = 5) => {
        try {
            const response = await getFeeds(page, size);
            setPagingFeed(response.data);
        } catch (error) {

        }
    }

    useEffect(() => {
        loadFeed();
    }, []);

    if (pagingFeed.content.length === 0)
        return (<div><div className="alert alert-danger text-center" role="alert">
            {t("There are no messages")}
        </div></div>)

    return (
        <div className='card mt-1 p-1'>
            {
                pagingFeed.content.map((feed: any) => { return <FeedItem key={feed.id} feed={feed.content} /> })
            }
            {!pagingFeed.last && (
            <div className="alert alert-dark text-center card-header" style={{cursor:"pointer"}}  role="alert">
                {t("Load old hoaxes")}
            </div>)}
        </div>



    );
};

export default FeedList;
