import { withTranslation } from "react-i18next";
import Input from "../component/Input";
import { useEffect, useState } from "react";
import ButtonWithLoading from "../component/ButtonWithLoading";
import { withApiProgress } from "../common/ApiProgress";
import { connect } from "react-redux";
import { loginHandleAction } from "../redux/authAction";

const LoginPage = (props: any) => {

    //static contextType: React.Context<any> = Authentication;

    const [username,setUsername] = useState();
    const [password,setPassword] = useState();
    const [error,setError]=useState();

    useEffect(()=>{
        setError(undefined);
    },[username,password]);


    const onClick = async (event: any) => {
        event.preventDefault();
        setError(undefined);
        const { history, loginHandle } = props as any;
        const cred = {
            username,
            password
        }
        try {
            const res = await loginHandle(cred); 
            history.push("/");

        } catch (err: any) {
            debugger;
            console.log(err);
            setError(err.response.data.message);
        }
    }


    const { t, pendingApiCall } = props as any;
    const enableLogin = !Boolean(username && password) && pendingApiCall;

    return (
        <div className="container">
            <h1 className="text-center">{t("Login")}</h1>
            <form>
                <Input name={"username"} label={t("Username")} onChange={(event:any) => setUsername(event.target.value)} />
                <Input name={"password"} label={t("Password")} onChange={(event:any) => setPassword(event.target.value)} type={"password"} />
                <ButtonWithLoading onClick={onClick} disabled={enableLogin} text={t("Login")} pendingApiCall={pendingApiCall} error={error} />
            </form>

        </div>
    )

}

const mapDispatchToProps = (dispatch: any) => {
    return {
        loginHandle: async (cred: any) => { dispatch(await loginHandleAction(cred)) }
    }
}
export default connect(null, mapDispatchToProps)(withApiProgress(withTranslation()(LoginPage), "/api/1.0/auth"));
