import { t } from 'i18next';
import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { useApiProgress } from '../common/ApiProgress';
import ButtonWithLoading from './ButtonWithLoading';
import ProfileImage from './ProfileImage';

const UserFeed = () => {


    const { image } = useSelector((store: any) => {
        return { image: store.image }
    })
    const [focused,setFocused] = useState(false);
    const [feed,setFeed] = useState("");
    const pendingApiCall = useApiProgress("post", "api/1.0/feed");


    return (
        <div className='card p-1 flex-row'>
            <div className='m-1'>
                <ProfileImage imageDb={image} width={32} />
            </div>
            <textarea 
            className='form-control' 
            rows={focused ? 5 : 1} 
            value={feed}
            onChange={(event:any)=> {setFeed(event.target.value)}}
            onFocus={() => {setFocused(true)}} 
            onBlur={()=> {setFocused(false)}}></textarea>
            <div className='m-1'>
                <ButtonWithLoading onClick={() => { }} disabled={pendingApiCall} pendingApiCall={pendingApiCall} text={t("Send")} />
            </div>

        </div>
    );
};

export default UserFeed;
