import React from "react";
import Input from '../component/Input';
import { signUp } from "../api/ApiCalls";
import { withTranslation } from "react-i18next";
import ButtonWithLoading from "../component/ButtonWithLoading";

class UserSignUp extends React.Component {

    state = {
        username: null,
        displayname: null,
        password: null,
        passwordrepeat: null,
        pendingApiCall: false,
        errors: {}
    }


    onChange = (event :any) => {
        const { name, value } = event.target;
        const errors :any= { ...this.state.errors }
        const { t } = this.props as any;
        errors[name] = undefined;
        if (name === 'password' || name === 'passwordrepeat') {
            if (name === 'password' && value !== this.state.passwordrepeat) {
                errors.passwordrepeat = t("Password mismatch");
            }
            else if (name === 'passwordrepeat' && value !== this.state.password) {
                errors.passwordrepeat = t("Password mismatch");
            }
            else {
                errors.passwordrepeat = undefined;
            }

        }
        this.setState({ [name]: value, errors });
    }

    onClick = async (event :any) => {
        this.setState({ pendingApiCall: true });
        const { username, displayname, password } = this.state;

        const data = {
            username,
            displayname,
            password
        }

        event.preventDefault();
        try {
            const response = await signUp(data);
            this.setState({ pendingApiCall: false });
        } catch (error:any) {
            console.log(error.response.data);

            if (error.response.data.validationerrors) { this.setState({ errors: error.response.data.validationerrors }); }
            this.setState({ pendingApiCall: false });
        }

        /* axios.post("/api/1.0/users", data).then(response => {
            this.setState({pendingApiCall:false});
        }).catch(error => {
            this.setState({pendingApiCall:false});
        }); */

    }



    render() {
        const { t } = this.props as any;
        const { pendingApiCall, errors } = this.state;
        const { username, displayname, password, passwordrepeat } = errors as any;
        return (
            <div className="container">
                <h1 className="text-center">{t("Sign Up")}</h1>
                <form>
                    <Input name={"username"} error={username} label={t("Username")} onChange={this.onChange} />
                    <Input name={"displayname"} error={displayname} label={t("Display Name")} onChange={this.onChange} />
                    <Input name={"password"} error={password} label={t("Password")} onChange={this.onChange} type={"password"} />
                    <Input name={"passwordrepeat"} error={passwordrepeat} label={t("Password Repeat")} onChange={this.onChange} type={"password"} />
                    <ButtonWithLoading onClick={this.onClick} disabled={pendingApiCall || passwordrepeat} text={t("Sign Up")} pendingApiCall={pendingApiCall}/>                  
                </form>
            </div>
        )
    }

}


export const UserSignUpPage = withTranslation()(UserSignUp);
