import { withTranslation } from "react-i18next";
import Input from "../component/Input";
import React from "react";
import { login } from "../api/ApiCalls";
import axios from "axios";

class LoginPage extends React.Component {

    state = {
        username: null,
        password: null,
        pendingApiCall: false,
        error: null
    }

    componentDidMount() {
        console.log("ilk yerleştirilmede");

        axios.interceptors.request.use(() => {
            this.setState({
                pendingApiCall: true
            });
        });

        axios.interceptors.response.use((response) => {
            this.setState({
                pendingApiCall: false
            });
            return response;
        }, (er) => {
            this.setState({
                pendingApiCall: false
            });
            debugger;
            throw er;
        })
    }

    onChange = (event: any) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
            error: null
        });
    }

    onClick = async (event: any) => {
        event.preventDefault();
        this.setState({ error: null });
        const { username, password } = this.state;
        const cred = {
            username,
            password
        }
        try {
            const res = await login(cred);
        } catch (err) {
            this.setState({
                error: (err as any).response.data.message
            });

        }
    }

    onChangeLanguage = (lang: any) => {
        const errors = {};
        this.setState({ errors });
        const { i18n } = this.props as any;
        i18n.changeLanguage(lang);
    }

    render() {
        const { t } = this.props as any;
        const { username, password, pendingApiCall, error } = this.state;

        const enableLogin = !Boolean(username && password) && pendingApiCall;

        return (
            <div className="container">
                <h1 className="text-center">{t("Login")}</h1>
                <form>
                    <Input name={"username"} label={t("Username")} onChange={this.onChange} />
                    <Input name={"password"} label={t("Password")} onChange={this.onChange} type={"password"} />
                    <div className="form-group text-center">
                        <button className="btn btn-primary" onClick={this.onClick} disabled={enableLogin}>{t("Login")}
                            {pendingApiCall && <span className="spinner-border spinner-border-sm"></span>}
                        </button>
                        {error && <div className="alert alert-danger" role="alert">
                            {error}
                        </div>}
                    </div>
                </form>

            </div>
        )
    }
}

export default withTranslation()(LoginPage);
