import axios from 'axios';
import React, { Component } from 'react';



export function withApiProgress(WrappedComponent: any, path:string) {
    return class ApiProgress extends Component {


        state = {
            pendingApiCall: false
        }

        updateState = (url: string, durum: boolean) => {

            if (path && url === path) {
                this.setState({
                    pendingApiCall: durum
                });
            }
        }


        componentDidMount() {

            axios.interceptors.request.use((request) => {

                this.updateState(request.url!, true);
                return request;
            });

            axios.interceptors.response.use((response) => {
                this.updateState(response.config.url!, false);
                return response;
            }, (er) => {
                this.updateState(er.config.url!, false);
                throw er;
            })
        }


        render() {

            const { pendingApiCall } = this.state as any;
            return (
                <div>
                    {/* {React.cloneElement(this.props.children, { pendingApiCall: this.state.pendingApiCall })} */}
                    <WrappedComponent pendingApiCall={pendingApiCall} />
                </div>
            );
        }
    }
}
