import { isContentEditable } from '@testing-library/user-event/dist/utils';
import React, { Component, useEffect, useState } from 'react';
import { useTranslation, withTranslation } from 'react-i18next';
import { getUsers } from '../api/ApiCalls';
import UserItem from './UserItem';

const UserList = (props: any) => {

    const [usersData, setUsersData] = useState({});
    useEffect(() => {
        const users = getUsers().then((response) => {
            setUsersData(response.data);
        })
    }, []);

    const { t } = useTranslation();


    const { content:users, first, last } = usersData as any;
    return (
        <div className='card'>
            <h3 className='card-header text-center'>{t("Users")}</h3>
            <div className='list-group-flush'>
                {
                    users && users.length > 0 &&
                    users.map((user: any) => {
                        return <UserItem key={user.username} user={user} />
                    })
                }
            </div>
            <div>
                {last === false && <button className='btn btn-sm btn-light'>Next</button>}
                {first === false && <button className='btn btn-sm btn-light float-right'>Previous</button>}
            </div>

        </div>
    );
}

export default UserList;
