import axios, { AxiosRequestConfig } from 'axios';
import { useEffect, useState } from 'react';

export const useApiProgress = (path: string) => {

    const [pendingApiCall, setPendingApiCall] = useState(false);

    const updateState = (url: string, status: boolean): void => {
        if (path === url) {
            setPendingApiCall(status);
        }

    }

    useEffect(() => {


        const requestId = axios.interceptors.request.use((request: AxiosRequestConfig) => {
            updateState(request.url!,true);
            return request;
        });

        const responseId = axios.interceptors.response.use((response) => {
            updateState(response.config.url!,false);
            return response;

        }, (error) => {
            updateState(error.config.url!,false);
            throw error;
        });
        return () => {
            axios.interceptors.request.eject(requestId);
            axios.interceptors.response.eject(responseId);
        }

    });

    return pendingApiCall;
}
