import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';


const ProfileCard = (props: any) => {

    // const context = useContext(Authentication);
    // const {state} = context as any ;
    // const { username } = state;

    let message = "YOU CONNOT EDIT";
    const routerParams = useParams();
    const { username, displayname } = useSelector((store: any) => {
        return { username: store.username, displayname: store.displayname }
    })

    const userPath: string = (routerParams as any).username;
    if (userPath === username) {
        message = "YOU CAN EDIT";
    }

    return (
        <div className='card'>
            {displayname + " - " + username}
        </div>
    );
};

export default ProfileCard;
