import { createStore } from "redux"
import { authReducer } from "./authReducer"


const initialState: any = {
    isLoggedIn: true,
    username: "user",
    password: undefined,
    displayname: undefined,
    image: undefined
  }


export const configureStore = () =>
{
    return createStore(authReducer);
}
